"use strict";export class LocalStorage{static set volume(e){localStorage.setItem("volume",""+e)}static get volume(){return localStorage.volume?Number(localStorage.volume):50}static set loopMode(e){localStorage.setItem("loopMode",""+e)}static get loopMode(){return localStorage.loopMode?Number(localStorage.loopMode):0}static set shuffleMode(e){localStorage.setItem("shuffleMode",""+e)}static get shuffleMode(){return localStorage.shuffleMode?Number(localStorage.shuffleMode):0}}
