"use strict";export class URLManager{static getSearchQuery(){return new URL(window.location.href).searchParams.get("search")||null}static setSearchQuery(e){const r=new URL(window.location.href);r.searchParams.set("search",e),window.history.pushState({},"",r)}static clearSearchQuery(){const e=new URL(window.location.href);e.searchParams.delete("search"),window.history.pushState({},"",e)}static isDebugMode(){return new URL(window.location.href).searchParams.get("debug")==="true"}}
